mapper.select:
SELECT a.id, a.after_service_code, c.name AS customerName, c.in_charge AS customerManager, i.name AS itemName, a.engineer, a.type, a.due_date AS dueDate, a.status, a.contents, i.last_inspect_date AS lastInspectDate,
CASE WHEN a.type = 'A' THEN '-' WHEN i.last_inspect_date IS NULL THEN '-' ELSE CAST(DATEDIFF(a.due_date, i.last_inspect_date) AS CHAR) END AS inspectCycleDays, CASE WHEN a.type = 'A' THEN 'AS' WHEN i.last_inspect_date IS NULL THEN '-' WHEN DATEDIFF(a.due_date, i.last_inspect_date) < 7 THEN CONCAT(DATEDIFF(a.due_date, i.last_inspect_date), '일') WHEN DATEDIFF(a.due_date, i.last_inspect_date) < 30 THEN CONCAT(FLOOR(DATEDIFF(a.due_date, i.last_inspect_date) / 7), '주') ELSE CONCAT(FLOOR(DATEDIFF(a.due_date, i.last_inspect_date) / 30), '개월') END AS inspectCycleLabel
FROM after_service a JOIN customer c ON c.id = a.cum_id JOIN item i ON i.id = a.item_id WHERE a.id = #{asId}
